import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";
import axios from "axios";

const API_KEY = "c4af9882a7587dd13ffdd6451d4880ea";
const BASE_URL = "https://api.themoviedb.org/3";

const fetchMovies = async (setMovies) => {
  try {
    const response = await axios.get(`${BASE_URL}/movie/popular?api_key=${API_KEY}&language=es-ES&page=1`);
    setMovies(response.data.results);
  } catch (error) {
    console.error("Error al cargar pelÃ­culas:", error);
  }
};

const MovieCard = ({ movie }) => (
  <div className="bg-gray-900 text-white rounded-2xl shadow-lg p-4 hover:scale-105 transition-all duration-300">
    <img className="rounded-xl mb-2" src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`} alt={movie.title} />
    <h2 className="text-xl font-bold mb-1">{movie.title}</h2>
    <p className="text-sm mb-1">â­ {movie.vote_average.toFixed(1)} / 10</p>
    <Link className="text-blue-400 hover:underline" to={`/movie/${movie.id}`}>Ver mÃ¡s</Link>
  </div>
);

const MovieList = () => {
  const [movies, setMovies] = useState([]);
  const [search, setSearch] = useState("");

  useEffect(() => {
    fetchMovies(setMovies);
  }, []);

  const filteredMovies = movies.filter((movie) =>
    movie.title.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="p-6">
      <input
        className="w-full p-2 rounded-xl mb-4 bg-gray-800 text-white"
        type="text"
        placeholder="Buscar pelÃ­culas..."
        value={search}
        onChange={(e) => setSearch(e.target.value)}
      />
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
        {filteredMovies.map((movie) => (
          <MovieCard key={movie.id} movie={movie} />
        ))}
      </div>
    </div>
  );
};

const MovieDetail = ({ id }) => {
  const [movie, setMovie] = useState(null);

  useEffect(() => {
    axios.get(`${BASE_URL}/movie/${id}?api_key=${API_KEY}&language=es-ES`)
      .then((res) => setMovie(res.data))
      .catch((err) => console.error(err));
  }, [id]);

  if (!movie) return <div className="text-white p-6">Cargando...</div>;

  return (
    <div className="p-6 text-white">
      <img className="w-64 rounded-xl mb-4" src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`} alt={movie.title} />
      <h1 className="text-3xl font-bold mb-2">{movie.title}</h1>
      <p className="mb-2">â­ {movie.vote_average.toFixed(1)} / 10</p>
      <p className="text-sm mb-4">{movie.overview}</p>
      <Link to="/" className="text-blue-400 hover:underline">â Volver</Link>
    </div>
  );
};

const Login = () => (
  <div className="text-white p-6">
    <h1 className="text-2xl font-bold mb-2">Inicio de sesiÃ³n</h1>
    <p>Este login es ficticio. Â¡Disfruta la app!</p>
  </div>
);

const App = () => (
  <Router>
    <div className="bg-black min-h-screen">
      <nav className="bg-gray-900 p-4 flex justify-between items-center text-white">
        <Link to="/" className="text-2xl font-bold">ð¬ CineCritix</Link>
        <Link to="/login" className="hover:underline">Login</Link>
      </nav>
      <Routes>
        <Route path="/" element={<MovieList />} />
        <Route path="/login" element={<Login />} />
        <Route path="/movie/:id" element={<MovieDetailWrapper />} />
      </Routes>
    </div>
  </Router>
);

const MovieDetailWrapper = () => {
  const id = window.location.pathname.split("/").pop();
  return <MovieDetail id={id} />;
};

export default App;
