// src/App.js
import { Film } from "lucide-react";

const movies = [
  {
    title: "Interstellar",
    description: "Una aventura épica más allá de la galaxia en busca del futuro de la humanidad.",
    rating: 5,
  },
  {
    title: "La La Land",
    description: "Un musical vibrante sobre el amor, la ambición y los sueños en Los Ángeles.",
    rating: 4,
  },
  {
    title: "El Padrino",
    description: "La legendaria historia de la familia mafiosa Corleone.",
    rating: 5,
  },
];

export default function App() {
  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-100 to-gray-300 p-4">
      <header className="text-center mb-10">
        <h1 className="text-4xl font-bold text-gray-800 flex justify-center items-center gap-2">
          CineCritix <Film className="w-8 h-8 text-purple-600" />
        </h1>
        <p className="text-sm text-gray-500">Tus críticas de cine favoritas</p>
      </header>

      <main className="max-w-md mx-auto space-y-6">
        {movies.map((movie, idx) => (
          <div
            key={idx}
            className="bg-white rounded-2xl shadow-md p-6 hover:scale-[1.02] transition-transform duration-300"
          >
            <h2 className="text-xl font-semibold text-gray-900">{movie.title}</h2>
            <p className="text-gray-600 mt-2">{movie.description}</p>
            <div className="mt-4 flex gap-1">
              {[...Array(5)].map((_, i) => (
                <span key={i} className={i < movie.rating ? "text-yellow-400" : "text-gray-300"}>
                  ★
                </span>
              ))}
            </div>
            <button className="mt-4 bg-purple-600 text-white px-4 py-2 rounded-full hover:bg-purple-700 text-sm">
              Ver más
            </button>
          </div>
        ))}
      </main>
    </div>
  );
}
