// src/App.jsx
import { useEffect, useState } from "react";
import { BrowserRouter as Router, Routes, Route, Link, useNavigate } from "react-router-dom";
import axios from "axios";
import { Moon, Sun, Star } from "lucide-react";
import { motion } from "framer-motion";

// Componente Navbar
const Navbar = ({ darkMode, toggleDarkMode }) => (
  <nav className={`p-4 flex justify-between items-center ${darkMode ? 'bg-gray-900' : 'bg-white'} shadow-md`}>
    <h1 className="text-2xl font-bold">ðŸŽ¬ CineCritix</h1>
    <div className="flex gap-4 items-center">
      <Link to="/" className="hover:underline">Inicio</Link>
      <Link to="/login" className="hover:underline">Login</Link>
      <button onClick={toggleDarkMode} className="p-2 rounded-full">
        {darkMode ? <Sun /> : <Moon />}
      </button>
    </div>
  </nav>
);

// Componente de Login ficticio
const Login = ({ setUser }) => {
  const navigate = useNavigate();
  const [input, setInput] = useState("");

  const handleLogin = () => {
    if (input.trim() !== "") {
      setUser(input.trim());
      navigate("/");
    }
  };

  return (
    <div className="p-6 text-center">
      <h2 className="text-xl mb-4">Inicia sesiÃ³n</h2>
      <input
        placeholder="Tu nombre"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        className="p-2 border rounded mb-4"
      />
      <br />
      <button onClick={handleLogin} className="bg-blue-600 text-white px-4 py-2 rounded">
        Entrar
      </button>
    </div>
  );
};

// Estrellas de calificaciÃ³n
const RatingStars = ({ rating }) => {
  const filled = Math.round(rating);
  return (
    <div className="flex gap-1">
      {[...Array(5)].map((_, i) =>
        i < filled ? <Star key={i} fill="gold" color="gold" /> : <Star key={i} color="gray" />
      )}
    </div>
  );
};

// PÃ¡gina de inicio con bÃºsqueda y lista de pelÃ­culas
const Home = ({ darkMode, user }) => {
  const [movies, setMovies] = useState([]);
  const [search, setSearch] = useState("");

  const fetchMovies = async () => {
    const res = await axios.get("https://api.themoviedb.org/3/movie/popular?api_key=<<API_KEY>>&language=es-ES");
    setMovies(res.data.results);
  };

  useEffect(() => {
    fetchMovies();
  }, []);

  const filtered = movies.filter(m => m.title.toLowerCase().includes(search.toLowerCase()));

  return (
    <div className="p-6">
      <h2 className="text-2xl font-semibold mb-4">Bienvenido {user || "ðŸ‘¤ Invitado"}</h2>
      <input
        type="text"
        placeholder="Buscar pelÃ­culas..."
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        className="p-2 rounded w-full md:w-1/2 mb-6 border"
      />
      <div className="grid md:grid-cols-3 gap-6">
        {filtered.map((movie) => (
          <motion.div
            key={movie.id}
            whileHover={{ scale: 1.05 }}
            className={`rounded-xl overflow-hidden shadow-lg ${darkMode ? 'bg-gray-800 text-white' : 'bg-white text-gray-900'}`}
          >
            <img src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`} className="w-full h-64 object-cover" />
            <div className="p-4">
              <h3 className="text-lg font-bold">{movie.title}</h3>
              <p className="text-sm mb-2">{movie.overview.slice(0, 100)}...</p>
              <RatingStars rating={movie.vote_average / 2} />
            </div>
          </motion.div>
        ))}
      </div>
    </div>
  );
};

// App principal
const App = () => {
  const [darkMode, setDarkMode] = useState(true);
  const [user, setUser] = useState("");

  return (
    <div className={darkMode ? "bg-gray-950 text-white min-h-screen" : "bg-gray-100 text-gray-900 min-h-screen"}>
      <Router>
        <Navbar darkMode={darkMode} toggleDarkMode={() => setDarkMode(!darkMode)} />
        <Routes>
          <Route path="/" element={<Home darkMode={darkMode} user={user} />} />
          <Route path="/login" element={<Login setUser={setUser} />} />
        </Routes>
      </Router>
    </div>
  );
};

export default App;
