import { useState, useEffect } from "react";
import { Film, Moon, Sun } from "lucide-react";

const movies = [
  {
    title: "Interstellar",
    description:
      "Una aventura épica más allá de la galaxia en busca del futuro de la humanidad.",
    rating: 5,
  },
  {
    title: "La La Land",
    description:
      "Un musical vibrante sobre el amor, la ambición y los sueños en Los Ángeles.",
    rating: 4,
  },
  {
    title: "El Padrino",
    description: "La legendaria historia de la familia mafiosa Corleone.",
    rating: 5,
  },
];

export default function App() {
  const [darkMode, setDarkMode] = useState(false);

  useEffect(() => {
    document.documentElement.classList.toggle("dark", darkMode);
  }, [darkMode]);

  return (
    <div className="min-h-screen bg-gray-100 dark:bg-gray-900 transition-colors">
      {/* Header */}
      <header className="flex justify-between items-center p-4 bg-white dark:bg-gray-800 shadow-md">
        <h1 className="text-3xl font-bold text-gray-800 dark:text-white flex items-center gap-2">
          <Film className="text-purple-600 w-7 h-7" /> CineCritix
        </h1>
        <button
          onClick={() => setDarkMode(!darkMode)}
          className="p-2 rounded-full bg-gray-200 dark:bg-gray-700 text-gray-800 dark:text-white"
        >
          {darkMode ? <Sun size={20} /> : <Moon size={20} />}
        </button>
      </header>

      {/* Main content */}
      <main className="max-w-4xl mx-auto py-10 px-4 grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
        {movies.map((movie, idx) => (
          <div
            key={idx}
            className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-5 transition-transform hover:scale-[1.03]"
          >
            <h2 className="text-xl font-semibold text-gray-900 dark:text-white">
              {movie.title}
            </h2>
            <p className="text-gray-600 dark:text-gray-300 mt-2">
              {movie.description}
            </p>
            <div className="mt-4 flex gap-1">
              {[...Array(5)].map((_, i) => (
                <span
                  key={i}
                  className={
                    i < movie.rating ? "text-yellow-400" : "text-gray-300 dark:text-gray-600"
                  }
                >
                  ★
                </span>
              ))}
            </div>
            <button className="mt-4 bg-purple-600 text-white px-4 py-2 rounded-full hover:bg-purple-700 text-sm">
              Ver más
            </button>
          </div>
        ))}
      </main>
    </div>
  );
}
