// src/App.js
import React from "react";
import { Star } from "lucide-react";

const peliculas = [
  {
    id: 1,
    titulo: "Interstellar",
    descripcion: "Una aventura épica más allá de la galaxia en busca del futuro de la humanidad.",
    puntuacion: 5,
  },
  {
    id: 2,
    titulo: "La La Land",
    descripcion: "Un musical vibrante sobre el amor, la ambición y los sueños en Los Ángeles.",
    puntuacion: 4,
  },
  {
    id: 3,
    titulo: "El Padrino",
    descripcion: "La legendaria historia de la familia mafiosa Corleone.",
    puntuacion: 5,
  },
];

const PeliculaCard = ({ titulo, descripcion, puntuacion }) => (
  <div className="bg-white text-gray-900 p-6 rounded-2xl shadow-lg w-full max-w-md">
    <h2 className="text-2xl font-bold mb-2">{titulo}</h2>
    <p className="text-sm mb-4">{descripcion}</p>
    <div className="flex gap-1">
      {Array.from({ length: 5 }).map((_, i) => (
        <Star
          key={i}
          className={`w-5 h-5 ${i < puntuacion ? "fill-yellow-400 text-yellow-400" : "text-gray-300"}`}
        />
      ))}
    </div>
  </div>
);

function App() {
  return (
    <div className="min-h-screen bg-gray-100 py-10 px-4 flex flex-col items-center gap-6">
      <h1 className="text-4xl font-bold text-gray-800">CineCritix 🎬</h1>
      {peliculas.map((pelicula) => (
        <PeliculaCard key={pelicula.id} {...pelicula} />
      ))}
    </div>
  );
}

export default App;
